configuration.alarmDetailsBuildJs:
var details = {};

if (metadata.prevAlarmDetails) {
    var prevDetails = JSON.parse(metadata.prevAlarmDetails);
    details.count = prevDetails.count + 1;
    details.value = prevDetails.value;
    details.time = prevDetails.time;
    details.name = prevDetails.name;
    details.CV = prevDetails.CV;
    details.contractor = prevDetails.contractor;
    details.type = prevDetails.type;
} else {
    details.value = msg.temperature;
    details.count = 1;
    details.time = msg.time;
    details.name = "Label No."+msg.label;
    details.CV = msg.truck;
    details.contractor = msg.contractor;
    if (details.value < -10) {
        details.type = "low temperature";
    } else {
        details.type = "height temperature";
    }
}

return details;

